import React, { useState } from "react";


const Card = (props) => {
  const [number, setNumber] = useState(1);
  const [quantity, setQuantity] = useState("half");

  const handleaddtoCart = () => {

  }

  const handleNumberChange = (e) => {
    setNumber(parseInt(e.target.value, 10));
  };

  const handleQuantityChange = (e) => {
    setQuantity(e.target.value);
  };

  let options = props.options;
  console.log(options);

    let priceoptions = Object.keys(options[0]);
    console.log(priceoptions);

  return (
     <div className="card mt-3 ms-5 col-4" style={{ width: "18rem" }}>
      <img src={props.img} className="card-img-top" alt="..." style={{height: "120px", objectFit: "fill"}}/>
      <div className="card-body">
        <h5 className="card-title">{props.name}</h5>
        <p className="card-text">{props.description}</p>
        <div className="container w-100">
          <select
            className="m-2 h-100  bg-success"
            onChange={handleNumberChange}
            value={number}
          >
            {Array.from(Array(6), (e, i) => (
              <option key={i + 1} value={i + 1}>
                {i + 1}
              </option>
            ))}
          </select>
          <select
            className="m-2 bg-success"
            onChange={handleQuantityChange}
            value={quantity}
          >
            {priceoptions
              .filter((data) => data !== '_id')
              .map((data) => (
                <option key={data} value={data}>
                  {data}
                </option>
              ))}
          </select>
          <div className='d-inline h-100 fs-5'>Total Price</div>

        </div>
        <hr></hr>
        <div className="btn bg-black text-success mx-1" onClick={handleaddtoCart}>Add to Cart</div>
      </div>
    </div>
  );
};

export default Card;
