import React,{useState} from 'react'
import {Link} from 'react-router-dom';



const Signup = () => {
    const {credentials, setcredentials} = useState(name="",  email="", password="")
    const handleSubmit = async(e) => {
        e.preventDefault();
        const response =  await fetch("http//:localhost:5000/api/createuser", {
            method:'POST',
            header:{
              'Content-type': 'application/json'

            },
            body:JSON.stringify({name:credentials.name, email:credentials.email, password:credentials.password})
         });
         const json = await response.json();
         console.log(json);
         if(!json.success)
         {
          alert("Enter valid  credentials");

         }
         const onChange = (event) => {
          setcredentials({... credentials,[event.target.name]: event.target.value});
         }
    }
  return (
    <>
      <div className="container">
      <form onSubmit={handleSubmit}>
  <div className="mb-3">
    <label htmlfor="name" className="form-label">Name</label>
    <input type="text" className="form-control" name="name" value={credentials.name} onChange={onChange}/>
    
  </div>
  <div class="mb-3">
    <label htmlfor="exampleInputEmail1" class="form-label">Email address</label>
    <input type="email" class="form-control" exampleInputEmail1="exampleInputEmail1"  value={credentials.email} onChange={onChange}/>
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div className="mb-3">
    <label htmlfor="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" id="exampleInputPassword1" value={credentials.password} onChange={onChange}/>
  </div>
  <div className="mb-3">
    <label htmlfor="exampleInputPassword1" className="form-label">branch</label>
    <input type="text" className="form-control" id="exampleInputPassword1" value={credentials.branch} onChange={onChange}/>
  </div>
 
  <button type="submit" className=" m-3 btn btn-primary">Submit</button>
  <Link to="/login" className="btn btn-danger">Already a User? </Link>
</form>
      </div>
    </>
  )
}

export default Signup
