import React from 'react';
import Delete from '@mui/icons-material/Delete';
import {useCart , useDispatchCart} from '../Components/ContextReducer';


export default function Cart(){
    let data = useCart();
    let dispatch = useDispatchCart();

    const handleCheckOut = async ()=>{
        let userEmail = localStorage.getItem("userEmail");
        let response = await fetch("http://localhost:5000/api/orderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                order_data: data,
                email: userEmail,
                order_date: new Date().toDateString()
            })
        })
        console.log("Order Response: ", response);
        if(response.status == 200)
        {
            dispatch({type: "DROP"})
        }
    }
   
    if(data.length == 0){
       return (
        <>
            <div className ='m-5 w-100 text-center fs-3'>The Cart is empty</div>
        </>
       )
    }
    else{
        let totalprice = data.reduce((total ,foodItem) => total+ foodItem.price, 0);
    return (
        <>
        {/* // {console.log("my college", data)} */}
            <div className = 'container m-auto mt-5 table responsive table-responsive-sm table-responsive-md'>
                <table className= 'table table-hover'>
                <thead className='text-success fs-4'>
                    <tr>
                        <th scope='col' > #</th>
                        <th scope='col'>Name</th>
                        <th scope='col'>Quantity</th>
                        <th scope='col'>Option</th>
                        <th scope='col'>Amount</th>
                        <th scope='col'></th>

                    </tr>
                </thead>
                <tbody>
                   {data.map((food, index)  =>(
                     <tr>
                        <th scope='row'>{index +1}</th>
                        <td>{food.name}</td>
                        <td>{food.qty}</td>
                        <td>{food.size}</td>
                        <td>{parseInt(food.price)}</td>
                        
                        <td><button type="button" className='btn p-0'><Delete onClick={ () => dispatch({type : "REMOVE", index: index})}/></button></td>
                     </tr>
                   ) )}
                </tbody>

                </table>
                <div><h1 className='fs-2'>Total Price: {totalprice}/-</h1></div>
                <div>
                    <button className="btn bg-success mt-5" onClick={handleCheckOut}>Check Out</button>
                </div>
            </div>
        </>
    )

    }

    
   
}
